},\"elements\":[],\"widgetType\":\"text-editor\"}],\"isInner\":false}],\"isInner\":false},{\"id\":\"d0f011b\",\"elType\":\"section\",\"settings\":[],\"elements\":[{\"id\":\"e6295a1\",\"elType\":\"column\",\"settings\":{\"_column_size\":100},\"elements\":[{\"id\":\"937cfd4\",\"elType\":\"widget\",\"settings\":{\"weight\":{\"unit\":\"px\",\"size\":3},\"color\":\"#6ec1e4\"},\"elements\":[],\"widgetType\":\"divider\"}],\"isInner\":false}],\"isInner\":false},{\"id\":\"d74e7fa\",\"elType\":\"section\",\"settings\":{\"structure\":\"30\"},\"elements\":[{\"id\":\"cc4231b\",\"elType\":\"column\",\"settings\":{\"_column_size\":33,\"_inline_size\":null},\"elements\":[],\"isInner\":false},{\"id\":\"01c0898\",\"elType\":\"column\",\"settings\":{\"_column_size\":33,\"_inline_size\":null},\"elements\":[{\"id\":\"5f3c9f1\",\"elType\":\"widget\",\"settings\":{\"title\":\"Why is an EEG performed?\",\"header_size\":\"h3\"},\"elements\":[],\"widgetType\":\"heading\"}],\"isInner\":false},{\"id\":\"01011fd\",\"elType\":\"column\",\"settings\":{\"_column_size\":33,\"_inline_size\":null},\"elements\":[],\"isInner\":false}],\"isInner\":false},{\"id\":\"869e92e\",\"elType\":\"section\",\"settings\":[],\"elements\":[{\"id\":\"a857ae4\",\"elType\":\"column\",\"settings\":{\"_column_size\":100},\"elements\":[{\"id\":\"5f2cd2e\",\"elType\":\"widget\",\"settings\":{\"weight\":{\"unit\":\"px\",\"size\":3},\"color\":\"#6ec1e4\"},\"elements\":[],\"widgetType\":\"divider\"}],\"isInner\":false}],\"isInner\":false},{\"id\":\"ab7256f\",\"elType\":\"section\",\"settings\":{\"structure\":\"21\"},\"elements\":[{\"id\":\"31fdee3\",\"elType\":\"column\",\"settings\":{\"_column_size\":33,\"_inline_size\":null},\"elements\":[{\"id\":\"eb3f094\",\"elType\":\"widget\",\"settings\":{\"editor\":\"<p>An EEG is used to detect problems in the electrical activity of the brain that may be associated with certain brain disorders. The measurements given by an EEG are used to confirm or rule out various conditions.<\\/p><p>When someone is in a coma, an EEG may be performed to determine the level of brain activity. The test can also be used to monitor activity during brain surgery<\\/p>\"},\"elements\":[],\"widgetType\":\"text-editor\"}],\"isInner\":false},{\"id\":\"e586ca4\",\"elType\":\"column\",\"settings\":{\"_column_size\":66,\"_inline_size\":null,\"text_align\":\"center\"},\"elements\":[{\"id\":\"a6ec8fe\",\"elType\":\"widget\",\"settings\":{\"icon_list\":[{\"text\":\"Seizure disorders (such as epilepsy)\",\"icon\":\"fa fa-check\",\"_id\":\"3eec333\",\"link\":{\"url\":\"\",\"is_external\":\"\",\"nofollow\":\"\"}},{\"text\":\"Head injury\",\"icon\":\"fa fa-check\",\"_id\":\"ca714ca\",\"link\":{\"url\":\"\",\"is_external\":\"\",\"nofollow\":\"\"}},{\"text\":\"Encephalitis (inflammation of the brain)\",\"icon\":\"fa fa-check\",\"_id\":\"013c03e\",\"link\":{\"url\":\"\",\"is_external\":\"\",\"nofollow\":\"\"}},{\"text\":\"Brain tumor\",\"icon\":\"fa fa-check\",\"link\":{\"url\":\"\",\"is_external\":\"\",\"nofollow\":\"\"},\"_id\":\"6ec6031\"},{\"text\":\"Encephalopathy (disease that causes brain dysfunction)\",\"icon\":\"fa fa-check\",\"link\":{\"url\":\"\",\"is_external\":\"\",\"nofollow\":\"\"},\"_id\":\"45aa032\"},{\"text\":\"Memory problems\",\"icon\":\"fa fa-check\",\"link\":{\"url\":\"\",\"is_external\":\"\",\"nofollow\":\"\"},\"_id\":\"49421fb\"},{\"text\":\"Sleep disorders\",\"icon\":\"fa fa-check\",\"link\":{\"url\":\"\",\"is_external\":\"\",\"nofollow\":\"\"},\"_id\":\"d19c692\"},{\"text\":\"Stroke\",\"icon\":\"fa fa-check\",\"link\":{\"url\":\"\",\"is_external\":\"\",\"nofollow\":\"\"},\"_id\":\"bf65c91\"},{\"text\":\"Dementia\",\"icon\":\"fa fa-check\",\"link\":{\"url\":\"\",\"is_external\":\"\",\"nofollow\":\"\"},\"_id\":\"f601de2\"}]},\"elements\":[],\"widgetType\":\"icon-list\"}],\"isInner\":false}],\"isInner\":false},{\"id\":\"be8001e\",\"elType\":\"section\",\"settings\":[],\"elements\":[{\"id\":\"0bbe048\",\"elType\":\"column\",\"settings\":{\"_column_size\":100},\